mapper.select:
select * from (
select rownum r_num, a.* from
(
select p.pdnum, p.rlettpcd, ps.tradtpcd, ps.prc, p.atclfetrdesc, p.taglist, p.rltrnm, pl.plocation  from product p, product_sale_info ps, product_location pl where p.pdnum = ps.pdnum and p.pdnum = pl.pdnum and p.rlettpcd ='D02'

		
			
				and p.atclfetrdesc like '%'|| #{searchValue} ||'%'
			
			
				and pl.plocation like '%'|| #{searchValue} ||'%'
			
		

order by p.pdnum desc
) a
) where r_num between #{start} and #{end}

select * from (
select rownum r_num, a.* from
(
select * from realprice 

	
		
			
				and gu like '%'|| #{searchValue} ||'%'
			
			
				and dong like '%'|| #{searchValue} ||'%'
			
		
	

) a
) where r_num between #{start} and #{end}

select count(*) cnt from realprice

			
		
			
				and gu = #{searchValue}
			
			
				and dong = #{searchValue}
