mapper.select:
select * from (select rownum r_num, a.* from (select p.pdnum , p.rlettpcd, ps.tradtpcd  as tradtpcd, p.atclfetrdesc, p.taglist, p.rltrnm, ps.prc, pl.plocation as plocation
from product p, product_sale_info ps, product_location pl 
where p.pdnum= pl.pdnum and p.pdnum = ps.pdnum order by p.pdnum desc) a ) where r_num between #{start} and #{end}

select * from (
select rownum r_num, a.* from
(
select * from product p, product_sale_info ps, product_location pl where p.pdnum = ps.pdnum and p.pdnum = pl.pdnum

		
			
				and ps.tradtpcd = #{searchValue}
			
			
				and pl.plocation like '%'|| #{searchValue} ||'%'
			
			
				and p.atclfetrdesc like '%'|| #{searchValue} ||'%'
			
		

order by p.pdnum desc
) a
) where r_num between #{start} and #{end}
